widgets.onValue:
var inputs = getProp(id, "inputs");
var baseAddress = getProp(id,"address");
for(var i in value)
{
  var inName = inputs[i];
  var add =  baseAddress+"/"+inName+"/index";
  var val = value[i].length > 0?value[i][0]:0;
  send(add,val);
}

tabs.onValue:
var baseAddress = getProp(id,"address");
for(var i in value)
{
var add =  baseAddress+"/"+i;
var val = value[i].length > 0?value[i][0]:0;
send(add,val);
}
